package Turma29;

public class operadoresaritimetricos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x = 9;
		int y = 2;
		
		
		int soma = x +y ;
		int subtraçao = x -y;
		int multiplicacao = x * y;
		int divisao = x / y;
		int resto = x % y ;
		
		System.out.println("resultado da adição" + soma );
		System.out.println("resultado da adição" + subtraçao );
		System.out.println("resultado da adição" +  multiplicacao);
		System.out.println("resultado da adição" + divisao );
		System.out.println("resultado da adição" + resto );
		
		
		x++;
		System.out.println("resultado do incremento x" + x);
		 
		y--;
		System.out.println("resultado do incremento y" + y);
		
		x +=10;
		System.out.println("resultado do incremento x" + x);
		
		y-= 10;
		System.out.println("resultado do incremento y + y");



		

				
				

	}

}
